class Solution {
public:
    int dp[10001][51];
    int md=1e9+7;
    int helper(int tar,vector<vector<int>>& vec,int i){
        if(tar==0) return 1;

        if(i>=vec.size()||tar<0) return 0;
        if(dp[tar][i]!=-1) return dp[tar][i];
        long long ans=0;
        for(int ii=1;ii<=vec[i][0];ii++){
            long long sum=tar-(ii*vec[i][1])%md;
            if(sum<0) break;

            ans=(ans+helper(sum,vec,i+1)%md)%md;
        }
        ans=(ans+helper(tar,vec,i+1)%md)%md;
        return dp[tar][i]=ans%md;
    }
    int waysToReachTarget(int tar, vector<vector<int>>& vec) {
        // sort(begin(vec),end(vec));
        memset(dp,-1,sizeof dp);
        return helper(tar,vec,0);
    }
};
