class Solution {
public:
    long long numberOfWays(string s) {
        unordered_map<long long, long long> prefix, suffix;
        for(long long i = 0; i < s.size(); i++) {
            suffix[s[i]]++;
        }
        prefix[s[0]]++;
        suffix[s[0]]--;
        long long ans = 0;
        for(long long i = 1; i < s.size() - 1; i++) {
            suffix[s[i]]--;
            if(s[i] == '1') {
                ans += (prefix['0'] * suffix['0']);
            }
            else {
                ans += (prefix['1'] * suffix['1']); 
            }
            prefix[s[i]]++;
        }
        return ans;
    }
};
