class Solution {
public:
    vector<string> findOcurrences(string text, string first, string second) {
         vector<string> v,ans;
        int i = 0;
        while(i < text.length()){
            string s =  "";
            while( i < text.length() and text[i] != ' '){
                s += text[i];
                i++;
            }
            i++;
            v.push_back(s);
        }
       for(int i=0;i<v.size();i++){
           if(v[i] == first and v[i+1] == second and i+2 < v.size()){
               ans.push_back(v[i+2]);

           }
       }
        return ans;




    }
};
