class Solution {
public:
    string oddString(vector<string>& words) {
        map<vector<int>, vector<string>> mp;
        for(auto s : words){
            vector<int> arr;
            for(int i = 0; i < s.length() - 1; i++){
                arr.push_back(s[i + 1] - s[i]);
            }
            mp[arr].push_back(s);
        }
        for(auto i : mp){
            if(i.second.size() == 1){
                return i.second[0];
            }
        }
        return "";
    }
};
