class Solution {
public:
    int partitionString(string s) {
        vector<int> arr(26, 0);
        int ans = 1;
        for(int i = 0; i < s.length(); i++){
            if(arr[s[i] - 'a'] == 1){
                arr.assign(26, 0);
                ans++;
            }
            arr[s[i] - 'a']++;
        }
        return ans;
    }
};
