const int MOD = 1e9 + 7;
int dp[55][55][55];
class Solution {
public:
    int helper(int m,int n,int startRow, int startColumn, int maxMoves){
        if(startRow < 0 || startRow >= m || startColumn < 0 || startColumn >= n)
            return 1;
        if(dp[startRow][startColumn][maxMoves] != -1)
            return dp[startRow][startColumn][maxMoves];
        if(maxMoves == 0)
            return 0;  
        long a = helper(m, n, startRow - 1, startColumn, maxMoves - 1)%MOD;
        long b = helper(m, n, startRow + 1, startColumn, maxMoves - 1)%MOD;
        long c = helper(m, n, startRow, startColumn - 1, maxMoves - 1)%MOD;
        long d = helper(m, n, startRow, startColumn + 1, maxMoves - 1)%MOD;
        return dp[startRow][startColumn][maxMoves] = (a + b + c + d)%MOD;
    }
    int findPaths(int m, int n, int maxMove, int startRow, int startColumn) {
        memset(dp, -1, sizeof dp);
        return helper(m,n,startRow, startColumn, maxMove);
    }
};
