int dp[105][25][105];
class Solution {
public:
    int helper(vector<int>&houses, vector<vector<int>> &cost, int ind, int prev, int target){
        if(target < 0)return 1e9;
        if(ind == houses.size()){
            if(target == 0){
                return 0;
            }else{
                return 1e9;
            }
        }
        if(dp[ind][prev + 1][target] != -1)return dp[ind][prev + 1][target];
        int ans = 1e9;
        if(houses[ind] != 0){
            if(houses[ind] == prev){
                ans = helper(houses, cost, ind + 1, prev, target);
            }else{
                ans = helper(houses, cost, ind + 1, houses[ind], target - 1);
            }
        }else{
            for(int i = 0 ; i < cost[ind].size();i++){
                if(i + 1 == prev){
                    ans = min(ans, cost[ind][i] + helper(houses, cost, ind + 1, prev, target));
                }else{
                    ans = min(ans, cost[ind][i] + helper(houses, cost, ind + 1, i + 1, target - 1));
                }
            }
        }
        return dp[ind][prev + 1][target] = ans;
    }
    int minCost(vector<int>& houses, vector<vector<int>>& cost, int m, int n, int target) {
        memset(dp, -1, sizeof dp);
        int ans = helper(houses, cost, 0, -1, target);
        return ans==1e9?-1:ans;
    }
};
