int dp[505][505];
class Solution {
public:
    int helper(vector<int> &cost, vector<int> &time, int ind, int left){
        if(left <= 0)return 0;
        if(cost.size() == ind)return 1e9;
        if(dp[ind][left] !=-1)return dp[ind][left];
        int f = helper(cost, time, ind + 1, left);
        int nf = helper(cost, time, ind + 1, left - time[ind] - 1) + cost[ind];
        return dp[ind][left] = min(f, nf);
    }
    int paintWalls(vector<int>& cost, vector<int>& time) {
        memset(dp, -1, sizeof dp);
        return helper(cost, time, 0, cost.size());
    }
};
