class Solution {
public:
int vis[60]={};
    int numPairsDivisibleBy60(vector<int>& time) {
        memset(vis,0,sizeof(vis));
        int res=0;
        for(int i =0;i<time.size();i++){
            int c= time[i]%60;
            res+=vis[(60-c)%60];
            vis[c]++;
        }
        return res;
    }
};
