class Solution {
public:
    bool isPalindrome(int x) {
        if(x == 0){
            return true;
        }else if(x<0){
            return false;
        }

        int temp = x;
        string s;
        while(temp > 0){
            s.push_back(temp%10);
            temp/=10;
        }

        int flag = 0;
        for(int i=0;i<s.length()/2;i++){
            if(s[i] != s[s.length()-i-1]){
                flag = 1;
            }
        }
        if(flag == 1){
            return false;
        }else{
            return true;
        }

    }
};
