int dp2[2005];
class Solution {
public:
   int helper(vector<vector<bool>> &dp, string &s,int ind){
        if(ind == s.length()){
            return 0;
        }
        if(dp2[ind] != -1)return dp2[ind];
        int ans = 1e9;
        for(int i = ind; i<s.length();i++){
            if(dp[ind][i]){
                ans = min(ans, 1 + helper(dp, s, i + 1));
            }
        }
        return dp2[ind] = ans;
    }
    int minCut(string s) {
        memset(dp2, -1, sizeof(dp2));
        vector<vector<bool>> dp(s.length(), vector<bool>(s.length(), false));
        int n = s.length();
        for(int i  = 0; i < n;i++)dp[i][i] = true;
        for(int i = 0; i < n - 1;i++)
            if(s[i] == s[i + 1]) dp[i][i + 1] = true;
        for(int i = n - 1; i >=0 ; i --){
            for(int j = i + 2; j < n;j++){
                if(s[i] == s[j] && dp[i+1][j-1])
                    dp[i][j] = true;
            }
        }
        return helper(dp, s, 0) - 1;
    }
};
