class Solution {
public:
    int countSubstrings(string s) {
        int n = s.length();
        bool dp[n][n];
        memset(dp, false, sizeof dp);
        for(int i = 0; i < n;i++)dp[i][i] = true;
        for(int i = 0; i < n-1;i++)if(s[i] == s[i+1])dp[i][i+1] = true;
        for(int i = n-3; i>=0;i--){
            for(int j = i + 2;j<n;j++){
                if(dp[i+1][j-1] && s[i] == s[j]){
                    dp[i][j] = true;
                }
            }
        }
        int cnt = 0;
        for(int i = 0; i < n;i++){
            for(int j=0;j<n;j++){
                if(dp[i][j])cnt++;
            }
        }
        return cnt;
    }
};
