class Solution {
public:
    int partitionDisjoint(vector<int>& nums) {
        int ans = 0;

        vector<int> rmin(nums.size()+1);
        rmin[nums.size()] = INT_MAX;

        for(int i =nums.size()-1;i>=0;i--){
            rmin[i] = min(nums[i] , rmin[i+1]);
        }

        int lmax = INT_MIN;

        for(int i = 0;i<nums.size();i++){
            lmax = max(nums[i] , lmax);
            if(lmax<=rmin[i+1]){
                ans = i+1;
                return ans;
            }
        }
        return ans;
    }
};
