class Solution {
public:
    int partitionArray(vector<int>& nums, int k) {
        sort(begin(nums),end(nums));
        reverse(begin(nums),end(nums));
        int cnt=1;
        int i=0;
        int f=0;
        while(i<nums.size()){
            if(nums[f]-nums[i]>k){
                f=i;
                cnt++;
            }else i++;
        }
        return cnt;
    }
};
