class Solution {
public:
    vector<int> partitionLabels(string s) {
        map<char, vector<int>>  m;
        for(int i = 0; i < s.length();i++){
            m[s[i]].push_back(i);
        }
        vector<vector<int>> arr;
        for(auto i : m){
            arr.push_back({i.second[0], i.second[i.second.size() - 1]});
        }
        sort(arr.begin(), arr.end());
        vector<int> curr = arr[0];
        vector<vector<int>> ans;
        for(int i = 1; i < arr.size();i++){
            if(curr[1] >= arr[i][0]){
                curr[1] = max(arr[i][1], curr[1]);
            }else{
                ans.push_back(curr);
                curr = arr[i];
            }
        }
        ans.push_back(curr);
        vector<int> res;
        for(auto i : ans){
            res.push_back(i[1] - i[0] + 1);
        }
        return res;
    }
};
