class Solution {
public:
    int minimumPartition(string s, int k) {
        int ans=0;
        long long int num=0;
        bool ispar=1;
        for(auto i:s){
            i=i-'0';
            num=num*10+i;
            if(num>k){
                // cout<<num<<" ";
                num=i;
                if(num>k) return -1;
                ispar=1;
                ans++;
            }else { ispar=0;}
        }
        ans++;
        return ans;

    }
};
