class Solution {
public:
    vector<int> getRow(int rowIndex) {
        if(rowIndex == 0)return{1};        
        if(rowIndex == 1)return{1,1};

        vector<int> temp = {1,1};
        for(int i=2;i<=rowIndex;i++){
            vector<int> arr{1};
            for(int j=0;j<temp.size() - 1;j++){
                arr.push_back(temp[j] + temp[j+1]);
            }
            arr.push_back(1);
            temp = arr;
        }
        return temp;
    }
};
