class Solution {
public:
    int peakIndexInMountainArray(vector<int>& arr) {
        int n = arr.size();
        arr.insert(arr.begin(), -1);
        arr.push_back(-1);
        int lo = 1, hi = n;
        while(lo <= hi){
            int mid = (lo + hi)/2;
            if(arr[mid - 1] < arr[mid] && arr[mid] > arr[mid + 1]){
                return mid - 1;
            }else if(arr[mid - 1] > arr[mid] && arr[mid + 1] < arr[mid]){
                hi = mid  - 1;
            }else{
                lo = mid + 1;
            }
        }
        return -1;
    }
};
