class Solution {
public:
    bool checkPerfectNumber(int num) {
        int ans =0;
        for(int i = 1; i <= sqrt(num);i++){
            if(num%i == 0){
                int x = i;
                int y = num/i;
                if(x == y)ans+= x;
                else ans += x+y;
            }
        }
        return num == ans - num;
    }
};
