const int N = 10005;
vector<int> dp(N + 1, 1e8);
class Solution {
public:

    int numSquares(int n) {
        if(dp[n] == 1e8){
            for(int i = 1; i <= sqrt(N) ;i++){
                dp[i * i] = 1;
            }
            for(int i = 2; i <= N; i ++){
                for(int j = i - 1;j>=1;j--){
                    dp[i] = min(dp[i], dp[j] + dp[i - j]);                                
                }
            }
        }        
        return dp[n];
    }
};
