class Solution {
public:
    int factorial(int n){
        int temp = 1;
        for(int i=2;i<=n;i++)
            temp *= i;
        return temp;
    }
     string helper(int n, int k, vector<int> &arr, string &s)
{
    if (n == 0)
        return s;
    int fact = factorial(n - 1);
    int ind = k / fact;
    s.push_back(arr[ind] + '0');
    arr.erase(arr.begin() + ind);
    return helper(n - 1, k % fact, arr, s);
}
