class Solution {
public:
    void helper(vector<int> &arr, vector<vector<int>> &ans, vector<int> &ds, vector<int> &visited){
        if(ds.size() == arr.size()){
            ans.push_back(ds);
            return;
        }
        for(int i=0;i<arr.size();i++){
            if(visited[i] == 1) continue;
            ds.push_back(arr[i]);
            visited[i] = 1;
            helper(arr, ans, ds,visited);
            ds.pop_back();
            visited[i] = 0;
        }
    }
    vector<vector<int>> permute(vector<int>& nums) {
        vector<vector<int>> ans;
        vector<int> temp;
        vector<int> visited(nums.size(), 0);
        helper(nums,ans,temp,visited);
        return ans;
    }
};
