class Solution {
public:
    vector<int> plusOne(vector<int>& digits) {
        reverse(digits.begin(), digits.end());
        int carry = 1;
        for(int i=0;i<digits.size();++i){
            digits[i]+=carry;
            carry = digits[i]/10;
            digits[i] %= 10;
        }
        while(carry > 0){
            digits.push_back(carry%10);
            carry/=10;
        }
        reverse(digits.begin(), digits.end());
        return digits;
    }
};
