class Solution {
public:
    vector<vector<int>> largeGroupPositions(string s) {
        int start = 0;
        vector<vector<int>> ans;
        char prev = s[0];
        for(int i = 0; i < s.length(); i++){
            if(s[i] != prev){
                if(i - start >= 3){
                    ans.push_back({start, i - 1});
                }
                start = i;
            }
            prev = s[i];
        }
        if(s.length() - start >= 3){
            ans.push_back({start, (int)s.length() - 1});
        }
        return ans;
    }
};
