typedef long long int ll;
const int MOD = 1e9 + 7;
ll add(ll a, ll b, ll m)
{
    return ((a % m) + (b % m)) % m;
}
ll mul(ll a, ll b, ll m)
{
    return ((a % m) * (b % m)) % m;
}
class Solution {
public:
    ll sumOfPower(vector<int>& nums) {
        ll ans = 0, prev = 0, curr = 0;
        sort(nums.begin(), nums.end());
        for(int i = 0; i < nums.size(); i ++){
            ll cube = mul(nums[i], mul(nums[i], nums[i], MOD), MOD);
            ans = add(ans, cube, MOD);
            ll temp = mul(nums[i], mul(nums[i], prev, MOD), MOD);
            ans = add(ans, temp, MOD);
            prev = mul(2, prev, MOD);
            prev = add(prev, nums[i], MOD);
        }
        return ans;
    }
};
