class Solution {
public:
    int projectionArea(vector<vector<int>>& v) {

        if(v.size() == 1 and v[0].size() == 1) return 2*v[0][0] + 1;
        int op1 = 0;
        for(auto x : v){
            for(auto y : x){
                if(y) op1++;
            }
        }



        for(int i=0;i<v.size();i++){
            int x1 = *max_element(v[i].begin(),v[i].end());            
            op1 += x1;

        }


        for(int i=0;i<v.size();i++){
            int x = 0;
            for(int j=0;j<v.size();j++){

                x = max(x,v[j][i]);

            }

            op1 += x;
        }



        return op1;
    }
};
