class Solution {
public:
    vector<int> arr;
    int sum = 0;
    Solution(vector<int>& w) {
        for(auto i : w){
            sum += i;
            arr.push_back(sum);
        }
    }

    int pickIndex() {
        return lower_bound(arr.begin(), arr.end(), rand()%sum + 1) - arr.begin();
    }
};

