class Solution {
public:
    const int m = 1e9 + 7;
    int rangeSum(vector<int>& nums, int n, int left, int right) {
        vector<long long> arr;
        for(int i = 0; i < nums.size(); i++){
            long long s = 0;
            for(int j = i; j < nums.size(); j++){
                s += nums[j]%m;
                arr.push_back(s);
            }
        }
        sort(arr.begin(), arr.end());
        long long ans = 0;
        for(int i = left - 1; i < right;  i++){
            ans += arr[i]%m;
        }
        return ans%m;
    }
};
