class Solution {
public:
    bool canConstruct(string ransomNote, string magazine) {
        unordered_map<char, int> m1;
        for(auto i : magazine){
            m1[i]++;
        }
        unordered_map<char, int> m2;
        for(auto i : ransomNote){
            m2[i]++;
        }
        for(auto i : m2){
            if(i.second > m1[i.first])return false;
        }
        return true;        
    }
};
