class Solution {
public:
    map<int, bool> vis;

    int cnt(int node, int parent, map<int, vector<int>> &g) {
        if(vis[node]) {
            return 0;
        }

        int ans = 1;
        for(auto i : g[node]) {
            if(i != parent) {
                ans += cnt(i, node, g);
            }
        }
        return ans;
    }
    int reachableNodes(int n, vector<vector<int>>& edges, vector<int>& restricted) {
        map<int, vector<int> > g;
        for(auto i : edges) {
            g[i[0]].push_back(i[1]);
            g[i[1]].push_back(i[0]);
        }
        for(auto i : restricted) 
        {
            vis[i] =  true;
        }        
        return cnt(0, -1, g);



    }
};
