class Solution {
public:
    vector<int> rearrangeArray(vector<int>& nums) {
        vector<int> p, n;
        for(auto i : nums)if(i>= 0)p.push_back(i);else n.push_back(i);
        int i = 0, j = 0;
        vector<int> arr = nums;
        for(int x = 0; x < nums.size();x++){
            if(x%2 == 0){
                arr[x] = p[i++];
            }else{
                arr[x] = n[j++];
            }
        }
        return arr;
    }
};
