class Solution {
public:
    int maxScore(vector<int>& arr) {
        vector<long long int> nums;
        for(auto i : arr)nums.push_back(1LL * i);
        sort(nums.begin(), nums.end(), greater<long long int>());
        long long int ans = 0;
        for(long long int i = 1; i < nums.size();i++){
            nums[i] += nums[i-1];
        }
        for(long long int i : nums){
            if(i > 0)ans++;
        }
        return ans;
    }
};
