class Solution {
public:
    int rearrangeCharacters(string s, string target) {
        char ch[27] = {0};
        for(auto i : s) {
            ch[i-'a']++;
        }
       int ans = INT_MAX;
        char ch1[27] = {0};

        for(auto i : target) {
            ch1[i- 'a']++;
        }

        for(auto i : target) {
            ans = min(ans, ch[i-'a'] / ch1[i - 'a']);
        }

    return ans;
    }
};
