class Solution {
public:
    string reorderSpaces(string text) {
        string ans = "", temp = "";
        vector<string> arr;
        int c = 0;
        for(auto i : text){
            if(i == ' '){
                c++;
                if(temp != "")
                    arr.push_back(temp);
                temp = "";
            }else{
                temp.push_back(i);
            }
        }
        if(temp != "")arr.push_back(temp);
        // cout<<c<<endl;
        // for(auto i : arr){
        //     cout<<i<<',';
        // }
        int n = arr.size();
        ans += arr[0];
        int r = c;
        if(n - 1 != 0){
            int s = c/(n - 1);
            r = c%(n - 1);
            for(int i = 1; i < n;i++){
                for(int j = 0; j < s;j++){
                    ans.push_back(' ');
                }
                ans += arr[i];
            }
        }
        while(r--)ans.push_back(' ');
        return ans;
    }
};
