class Rect{
public:
    pair<int,int> tl, tr, bl, br;
    Rect(){

    }
    Rect(int ax1, int ay1, int ax2, int ay2){
        bl = {ax1, ay1};
        tr = {ax2, ay2};
        tl = {ax1, ay2};
        br = {ax2, ay1};
    }
};
class Solution {
public:
    int computeArea(int ax1, int ay1, int ax2, int ay2, int bx1, int by1, int bx2, int by2) {
        Rect *A = new Rect(ax1, ay1, ax2, ay2); 
        Rect *B = new Rect(bx1, by1, bx2, by2); 
        Rect *C = new Rect();
        C->tl = {max(A->tl.first, B->tl.first), min(A->tl.second, B->tl.second)};
        C->tr = {min(A->tr.first, B->tr.first), min(A->tr.second, B->tr.second)};


        C->bl = {max(A->bl.first, B->bl.first), max(A->bl.second, B->bl.second)};
        C->br = {min(A->br.first, B->br.first), max(A->br.second, B->br.second)};

        int _A = (A->tl.second - A->bl.second) * (A->br.first - A->bl.first);
        int _B = (B->tl.second - B->bl.second) * (B->br.first - B->bl.first);
        int _C = (C->tl.second - C->bl.second) * (C->br.first - C->bl.first);

        if((A->br.first < B->bl.first) ||(B->br.first < A->bl.first) ||
            (A->tr.second < B->br.second) || (B->tr.second < A->br.second)){
            _C = 0;
        }
        // cout<<C->tl.first<<" "<<C->tl.second<<endl;
        // cout<<C->tr.first<<" "<<C->tr.second<<endl;
        // cout<<C->bl.first<<" "<<C->bl.second<<endl;
        // cout<<C->br.first<<" "<<C->br.second<<endl;
        return _A + _B - _C;
    }
};
