class Rect{
public:
    pair<int,int> tl, tr, bl, br;
    Rect(vector<int> &arr){
        bl = {arr[0], arr[1]};
        tr = {arr[2], arr[3]};
        tl = {arr[0], arr[3]};
        br = {arr[2], arr[1]};
    }
};
class Solution {
public:
    bool isRectangleOverlap(vector<int>& rec1, vector<int>& rec2) {
        Rect *A = new Rect(rec1);
        Rect *B = new Rect(rec2);
        if((A->br.first <= B->bl.first) ||(B->br.first <= A->bl.first) ||
            (A->tr.second <= B->br.second) || (B->tr.second <= A->br.second)){
            return false;
        }
        return true;
    }
};
