class Solution {
public:
    bool makeEqual(vector<string>& A) {
        map<char, int> m;
        for(string i : A){
            for(auto j : i) m[j]++;
        }
        int n = A.size();

        for(auto i : m){
            if(i.second%n != 0) return false;
        }
        return true;
    }
};
