class Solution {
public:
    int minSetSize(vector<int>& arr) {

       unordered_map<int, int> m;
        for(auto i : arr) m[i]++;

        priority_queue<int> q;
        for(auto i: m) {
            q.push(i.second);
        }

        int ans = arr.size();
        int cnt = 0;

        while(!q.empty()) {
           int top = q.top();
            q.pop();
            ans -= top;
            cnt++;
            if(ans <= arr.size() / 2) break;
        }
        return cnt;
    }
};
