class Solution {
public:
    int reductionOperations(vector<int>& nums) {
        if(nums.size() == 0 || nums.size() == 1) return 0; 
        int ind = 0;
        int n = nums.size();
        for(int i=0;i<n-1;i++){
            if(nums[i] != nums[i+1]) ind = 1;
        }
        if(ind == 0) return 0;

        map<int, int> m;
        sort(nums.begin(), nums.end());
        int x  = nums[0];
        int y =0;
        for(int i=0;i<n;i++){
            if(nums[i] > x){
               y = nums[i];
                break;
            }
        }

        vector<int> v;

        int cnt = 0;

        for(auto i : nums) m[i]++;
        for(auto i : m){
            v.push_back(i.first);        }
        while(m[y]!=0){
            int maxi = v[v.size()-1];
            int just = v[v.size()-2];
            cnt += m[maxi];
            m[just] += m[maxi];
            m[maxi] = 0;
            v.pop_back();


        }
        return cnt;


    }
};
