class Solution {
public:
    bool helper(string &s, string &p, int i, int j){
        if(i == -1 && j == -1)return true;
        if(j == -1) return false;
        if(i == -1){
            while(j> 0 &&p[j] == '*'){
                j-=2;
            }
            return j <= -1;
        }
        if(s[i] == p[j] || p[j] == '.'){
            return helper(s, p, i - 1, j - 1);
        }
        else if(p[j] == '*'){
             if (s[i] == p[j - 1] || p[j - 1] == '.')
            {
                bool notTake = helper(s, p, i , j - 2);
                bool take = helper(s, p, i - 1, j);
                return (take || notTake);
            }
            else{
                return helper(s, p, i, j - 2);
            }
        }else{
            return false;
        }
    }
    bool isMatch(string s, string p) {
        return helper(s, p, s.length() - 1, p.length() - 1);
    }
};
