class Solution {
public:
    vector<int> relativeSortArray(vector<int>& arr1, vector<int>& arr2) {
        // sort(arr1.begin(),arr1.end());
        vector<int> ans;
        map<int, int> m;
        for(auto i : arr1){
            m[i]++;
        }

      for(auto i : arr2){
          int x = m[i];
          while(x){
              ans.push_back(i);
              x--;
          }

          m.erase(i);
      }

        for(auto i : m){

            int x = i.second;
            while(x--)
            ans.push_back(i.first);
        }

        return ans;
    }
};
