class Solution {
public:
    vector<int> relocateMarbles(vector<int>& nums, vector<int>& moveFrom, vector<int>& moveTo) {
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        for(int i = 0; i < moveFrom.size(); i++){
            int a = mp[moveFrom[i]];
            mp.erase(moveFrom[i]);
            mp[moveTo[i]]+=a;
        }
        vector<int> ans;
        for(auto i : mp)ans.push_back(i.first);
        return ans;
    }
};
