class Solution {
public:
    string removeDuplicates(string s, int k) {
          stack<pair<char, int> > st;
        for(int i =0;i<s.length();i++) 
        {
            if(st.empty()) {
                st.push({s[i], 1});
            }
            else {
                if(s[i] == st.top().first) {
                    st.top().second++;
                }
                else {
                    st.push({s[i], 1});
                }
            }

            if(st.top().second == k) {
                st.pop();
            } 
        }

        string ans = "";
        while(!st.empty()) {
            pair<char, int> p = st.top();
            while(p.second--) {
                ans += p.first;
            }
            st.pop();
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
};
