class Solution {
public:
    string removeDuplicateLetters(string s) {
      unordered_map<char, int> cnt;
        unordered_map<char, bool> vis;
        for(auto i: s) {
            cnt[i]++;
        }
        stack<char> st;
        for(auto i : s) {
            cnt[i]--;
            if(vis[i]) {
                continue;
            }
            while(!st.empty() and (i < st.top()) and cnt[st.top()] > 0) {
                vis[st.top()] = false;
                st.pop();
            }
            st.push(i);
            vis[i] = true;
         }
        string ans;
        while(!st.empty()) {
            ans.push_back(st.top());
            st.pop();
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
};
