class Solution {
public:
    int removeDuplicates(vector<int>& nums) {
        map<int, int> umap;
        for(auto i : nums){
            umap[i]++;
        }
        int k = 0;
        for(auto i : umap){
            for(int j = 0; j < min(2, i.second);j++){
                nums[k++] = i.first;
            }
        }
        return k;        
    }
};
