class Solution {
public:
    string removeKdigits(string num, int k) {
        stack<int> st;
        for (int i = 0; i < num.size() ; i++)
        {
            while (k > 0 && !st.empty() && num[st.top()] > num[i])
            {
                st.pop();
                k--;
            }
            st.push(i);
        }
        while (k > 0)
        {
            st.pop();
            k--;
        }
        string ans = "";
        while (!st.empty())
        {
            ans.push_back(num[st.top()]);
            st.pop();
        }
        reverse(ans.begin(), ans.end());
        cout<<ans<<" ";
        if(ans == "")return "0";
        int i = 0;
        for(;i<ans.size();i++){
            if(ans[i] != '0')break;
        }
        if(i == ans.size())return "0";

        return ans.substr(i, ans.size() - i);
    }
};
