class Solution {
public:
    bool equalFrequency(string word) {
        for(int i = 0; i < word.size(); i++){
            map<char, int> mp;
            int _min = 1e9, _max = -1e9;
            for(int j = 0; j < word.size(); j++){
                if(i == j)continue;
                mp[word[j]]++;
            }
            for(auto i : mp){
                _max = max(i.second, _max);
                _min = min(_min, i.second);
            }
            if(_min == _max)return true;
        }
        return false;
    }
};
