class Solution {
public:
    string removeOuterParentheses(string s) {
        stack<char>st;
        string ans = "";
        for(int i = 0;i<s.size();i++){
            char ch = s[i];
            if(st.empty()){
                if(ch == '(')st.push(ch);
            }else{
                if(ch == '('){
                    st.push(ch);
                    ans+=ch;
                }else{
                    st.pop();
                    if(!st.empty())ans+=ch;
                }

            }
        }
        return ans;
    }
};
