class Solution {
public:
    int minStoneSum(vector<int>& piles, int k) {

        priority_queue<int> q;
        for(auto i : piles) {
            q.push(i);
        }

        while(k--) {
            int t = q.top();
            q.pop();

            // cout << "t " << t << endl;
            q.push(t - t / 2);
        }

        int ans = 0;

        while(!q.empty()) {
            ans += q.top();
            q.pop();
        }
        return ans;

    }
};
