class Solution {
public:
    long long minimumRemoval(vector<int>& b) {
        sort(b.begin(),b.end());
        signed long long ans=LLONG_MAX;
        signed long long m=b[0];
        signed long long temp=0;
        for(int i=b.size()-1;i>=0;i--){
            temp+=b[i];
        }
        signed long long carry=0;
        int n=b.size()-1;
        signed long long prev=0;
        for(int i=0;i<b.size();i++){
            if(i==n){
                ans=min(ans,carry);
                break;
            }
            signed long long k=((n-i+1)*(b[i]-prev));
            temp-=k;
            // cout<<temp<<endl;
            ans=min(ans,carry+temp);
            carry+=b[i];
            prev=b[i];
        }
        return ans;
    }
};
